import React, {useState} from "react";
import "./About.css";

const UrL = ("http://localhost:8004/docs")


function PostData({addContent}) {
    const [content, setDocs] = useState("");

    const handleSubmit = (e) => {
        const docs = {content}
        e.preventDefault();
        fetch(UrL, {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(docs),
        })
          .then((r) => r.json())
          .then((newContent) => addContent(newContent));

        setDocs("");

     }

  return (
  <div>
       <form onSubmit={handleSubmit}>
  <input onChange={(e) => setDocs(e.target.value)} className='inpt' value={docs} />
    <input className='btn'
        type="submit" 
        value="Add your comment" 
      />
</form>

  </div>
  );
  }

export default PostData;
