import React, {useState, useEffect} from 'react';
import "./About.css";


const UrL = ("http://localhost:8004/docs")
const About = () => {
  const [data, setDocs] = useState([]);

  useEffect(() => {
    fetch(UrL)
      .then((response) => response.json())
      .then((data) => {
        setDocs(data.people);
     });

  }, []);

  return (
    <section className='about'>
      <div className='container'>
        <div className='section-title'>
          <h2>About</h2>
          <p className='fs-17'>Search any book of your choice by inserting the title of the book in the Search and wait for response.</p>

        </div>

        <div className='about-content grid'>
          <div className='about-img'>

          </div>

        </div>
        <div className='formdata'>
        <form>
  <input className='inpt' type="text" name="name" />
    <button className='btn' type="submit" > Comment</button> 
</form>
  </div>
        {data.map((docs) => docs.name)}
      </div>
    </section>
  )
}

export default About
