import React, {useState, useEffect} from 'react';
import PostData from './PostData';
import "./About.css";


const UrL = ("http://localhost:8004/docs")

function Fetch() {
    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [docs, setItems] = useState([]);

    useEffect(() => {
        fetch(UrL)
          .then(res => res.json())
          .then(
            (result) => {
              setIsLoaded(true);
              setItems(result);
            },

            (error) => {
              setIsLoaded(true);
              setError(error);
            }
          )
      }, [])
   const addDocs = (newDocs) => {
          setItems([...docs, newDocs]);
        }

      if (error) {
        return <div>Error: {error.message}</div>;
      } else if (!isLoaded) {
        return <div>Loading...</div>;
      } else {





  return ( 
    <div >
      <PostData onAddDocs = {addDocs}  />

{docs.map(doc => (
          <p className='inputs' key={doc.key}>
          {doc.name} :  {doc.content} <button  className='remv'>X</button>
            </p>

))}

   </div>



 );
}
}

export default Fetch;
