import React, {useState, useEffect} from 'react';
import "./About.css";


const UrL = ("http://localhost:8004/docs")

function Fetch() {
    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [docs, setItems] = useState([]);
    const [Name, setDocs] = useState("");
    const [content, setData] = useState ("")

    useEffect(() => {
        fetch(UrL)
          .then(res => res.json())
          .then(
            (result) => {
              setIsLoaded(true);
              setItems(result);
            },

            (error) => {
              setIsLoaded(true);
              setError(error);
            }
          )
      }, [])


      if (error) {
        return <div>Error: {error.message}</div>;
      } else if (!isLoaded) {
        return <div>Loading...</div>;
      } else {

        const handleSubmit = (e) => {
          e.preventDefault();
          fetch(UrL, {
             method: 'POST',
             body: JSON.stringify({
                Name: Name,
                content: content


             }),
             headers: {
                'Content-type': 'application/json; charset=UTF-8',
                'Accept': 'application/json'
             },
          })
             .then((res) => res.text())
             .then((doc) => {
                setItems((docs) => [doc, ...docs]);
                setDocs("");
                setData("");
             })
             .catch((err) => {
              console.log(err.message);
           });
       };



  return ( 
    <>
      <div className="abt">
       <form onSubmit={handleSubmit}>
        <input onChange={(e) => setData(e.target.value)} className="int" placeholder="name.." value={data} />
