import React, {useState} from "react";
import "./About.css";


const UrL = ("http://localhost:8004/docs")

function PostData({addDocs}) {
    const [info, setDocs] = useState("");
    const [data, setData] = useState ("")

    const handleSubmit = (e) => {
      e.preventDefault();
      fetch(UrL, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          info,
          data
         }),
      })
        .then((r) => r.json())
        .then((newData) => addDocs(newData));

      setDocs("");
      setData("");
     }



  return (
  <div className="abt">
       <form onSubmit={handleSubmit}>
        <input onChange={(e) => setData(e.target.value)} className="inpt" placeholder="name.." value={data} />
  <input onChange={(e) => setDocs(e.target.value)} className='inpt' value={info} />
    <button className='btn'
        type="submit" 
        > Add your comment</button>

</form>

  </div>
  );
  }

export default PostData;
